import React, { useEffect, useState } from 'react';
import './App.css';


interface Sub {
  nick: string
  subMonth: number
  avatar: string
  description?: string
}

interface AppSate {
  subs: Array<Sub>
  newSubsNumber: number
}
const INITIAL_STATE = [
  {
    nick: 'dapelu',
    subMonth: 3,
    avatar: 'https://i.pravatar.cc/150?u=dapelu',
    description: 'Dapeli hace de moderador'
  },
  {
    nick: 'sergio_serrano',
    subMonth: 7,
    avatar: 'https://i.pravatar.cc/150?u=sergio_serrano'
  }
]

function App() {
  const [subs, setSubs] = useState<AppSate["subs"]>([])
  const [newSubsNumbers, senewSubsNumbers] = useState<AppSate["subs"]>([])

  useEffect(() => {
    setSubs(INITIAL_STATE)
  }, [])


  return (
    <div className="App">
      <h1>Subs</h1>
      <ul>
        {
          subs.map(sub => {
            return (
              <li key={sub.nick}>
                <img src={sub.avatar} alt='Avatar' />
                <h4>{sub.nick} (<small>{sub.subMonth}</small>)</h4>
                <p>{sub.description?.substring(0, 100)}</p>
              </li>
            )
          })
        }
      </ul>
    </div>
  );
}

export default App;
